mapper.select:
SELECT NB_ID, BBS_CATE, TTL, CN, WRTR, REG_DTM FROM NOTICE
WHERE BBS_CATE=#{BBS_CATE}
ORDER BY REG_DTM DESC, NB_ID DESC

mapper.update:
UPDATE NOTICE
SET BBS_CATE = #{BBS_CATE}
    , TTL = #{TTL}
    , CN = #{CN}
    , WRTR = #{WRTR}
WHERE NB_ID = #{NB_ID}

UPDATE NOTICE
SET   VIEWS = VIEWS + 1
WHERE NB_ID = #{NB_ID}
